import React, { useState } from "react";
import { Descriptions, Image, Modal } from "antd";
import "./marker.style.scss";
import { Marker } from "react-google-maps";
import { MarkerProps, MarkerTypes } from "./marker.types";

const MapMarker: React.FC<MarkerProps> = (props) => {
  const { text, location } = props;
  const [thisLocation, setThisLocation] = useState<MarkerTypes | boolean>(
    false
  );

  return (
    <>
      <Marker
        position={{ lat: location.lat, lng: location.lng }}
        onClick={() => setThisLocation(location)}
      >
        <img src={text} alt="pin" className="pin-image" />
      </Marker>

      <Modal
        title="Location Information"
        visible={!!thisLocation}
        onOk={() => setThisLocation(false)}
        onCancel={() => setThisLocation(false)}
        cancelText="Close"
        okButtonProps={{ hidden: true }}
        className="flex-Center"
      >
        <Descriptions size="small" bordered>
          <Descriptions.Item label="Logo" span={3}>
            {thisLocation &&
            typeof thisLocation !== "boolean" &&
            thisLocation?.banner ? (
              <Image
                width={100}
                height={100}
                src={thisLocation?.banner}
                alt="banner"
                fallback="data:image/png;base64,"
              />
            ) : (
              ""
            )}
          </Descriptions.Item>
          <Descriptions.Item label="Name" span={3}>
            {thisLocation &&
            typeof thisLocation !== "boolean" &&
            thisLocation?.title
              ? thisLocation?.title
              : ""}
          </Descriptions.Item>
          <Descriptions.Item label="Description" span={3}>
            {thisLocation &&
            typeof thisLocation !== "boolean" &&
            thisLocation?.description
              ? thisLocation.description
              : ""}
          </Descriptions.Item>
          <Descriptions.Item label="Type" span={3}>
            {thisLocation &&
            typeof thisLocation !== "boolean" &&
            thisLocation?.type
              ? thisLocation?.type
              : ""}
          </Descriptions.Item>
        </Descriptions>
      </Modal>
    </>
  );
};

export default MapMarker;
